package basiccon;

public class PrimitiveDataType {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte age =21;
		int phone =123456789;
		long phones = 123456789000L;
		float pi = 3.14F;
		char letter ='@';
		boolean isAdult = true;
		
		System.out.println(age+" "+phone+" "+phones+" "+pi+" "+letter+" "+isAdult);

	}


}
